private void ConfigureDataGridViewColumns(DataGridView dataGridView)\n{\n    foreach (DataGridViewColumn column in dataGridView.Columns)\n    {\n        var property = typeof(Baz).GetProperty(column.DataPropertyName);\n        if (property != null)\n        {\n            var attr = property.GetCustomAttribute<DataGridViewColumnTitleAttribute>();\n            if (attr != null)\n            {\n                column.HeaderText = attr.Title;\n            }\n        }\n    }\n}