using System.Data.Linq;\nusing System.Data.Linq.Mapping;\n\n// Assuming you have DataContext and your DBML classes set up.\nDataContext context = new YourDataContext();\n\n// Set up DataLoadOptions for delayed loading.\nDataLoadOptions options = new DataLoadOptions();\n// Specify which associations should be loaded deferred.\noptions.LoadWith<YourTableType>(t => t.BlobFieldAssociation);\n\ncontext.LoadOptions = options;\n\nvar result = from item in context.YourTable\n             select item;\n\nforeach (var item in result)\n{\n    // At this point, blob fields are not loaded unless explicitly accessed.\n    // Accessing the property will load the data.\n    var blobData = item.BlobFieldAssociation;\n}