using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\npublic class Data\n{\n    public int theID { get; set; }\n    public int theGroup { get; set; }\n    public string theValue { get; set; }\n}\n\npublic class Result\n{\n    public int ID { get; set; }\n    public string CHOICE { get; set; }\n    public int COUNT { get; set; }\n    public int SORT { get; set; }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        var data = new List<Data>\n        {\n            new Data { theID = 1, theGroup = 2, theValue = "Red" },\n            new Data { theID = 2, theGroup = 2, theValue = "Blue" },\n            new Data { theID = 3, theGroup = 2, theValue = "Green" },\n            new Data { theID = 1, theGroup = 3, theValue = "10" },\n            new Data { theID = 2, theGroup = 3, theValue = "24" },\n            new Data { theID = 3, theGroup = 3, theValue = "30" },\n            new Data { theID = 1, theGroup = 4, theValue = "1" },\n            new Data { theID = 2, theGroup = 4, theValue = "2" },\n            new Data { theID = 3, theGroup = 4, theValue = "3" }\n        };\n\n        var result = data\n            .GroupBy(d => d.theID)\n            .Select(g => new Result\n            {\n                ID = g.Key,\n                CHOICE = g.FirstOrDefault(x => x.theGroup == 2)?.theValue,\n                COUNT = Convert.ToInt32(g.FirstOrDefault(x => x.theGroup == 3)?.theValue),\n                SORT = Convert.ToInt32(g.FirstOrDefault(x => x.theGroup == 4)?.theValue)\n            })\n            .ToList();\n\n        foreach (var item in result)\n        {\n            Console.WriteLine($"{item.ID} {item.CHOICE} {item.COUNT} {item.SORT}");\n        }\n    }\n}