using System;\nusing System.Collections.Generic;\nusing System.Web.Profile;\nusing Newtonsoft.Json;\n\npublic class ShoppingCartItem\n{\n    public string ProductId { get; set; }\n    public int Quantity { get; set; }\n}\n\n[Serializable]\npublic class ShoppingCart\n{\n    public List<ShoppingCartItem> Items { get; set; }\n    \n    public ShoppingCart()\n    {\n        Items = new List<ShoppingCartItem>();\n    }\n    \n    // Method to add items, etc.\n    public void AddItem(ShoppingCartItem item)\n    {\n        Items.Add(item);\n    }\n}\n\npublic class ProfileManager\n{\n    public static void SaveCartToProfile(string userName, ShoppingCart cart)\n    {\n        string jsonCart = JsonConvert.SerializeObject(cart);\n        UserProfile profile = (UserProfile)ProfileBase.Create(userName);\n        profile.SetPropertyValue("ShoppingCart", jsonCart);\n        profile.Save();\n    }\n\n    public static ShoppingCart LoadCartFromProfile(string userName)\n    {\n        UserProfile profile = (UserProfile)ProfileBase.Create(userName);\n        string jsonCart = (string)(profile.GetPropertyValue("ShoppingCart") ?? string.Empty);\n\n        if (!string.IsNullOrEmpty(jsonCart))\n        {\n            return JsonConvert.DeserializeObject<ShoppingCart>(jsonCart);\n        }\n\n        return new ShoppingCart();\n    }\n}