using System;\nusing System.Data;\n\nclass Program\n{\n    static void Main()\n    {\n        DataSet ds = new DataSet("SampleDataSet");\n\n        DataTable customersTable = new DataTable("Customers");\n        DataColumn customerIDColumn = customersTable.Columns.Add("CustomerID", typeof(int));\n        customersTable.PrimaryKey = new DataColumn[] { customerIDColumn };\n        customersTable.Columns.Add("CustomerName", typeof(string));\n\n        DataTable ordersTable = new DataTable("Orders");\n        DataColumn orderIDColumn = ordersTable.Columns.Add("OrderID", typeof(int));\n        ordersTable.PrimaryKey = new DataColumn[] { orderIDColumn };\n        ordersTable.Columns.Add("CustomerID", typeof(int)); // Foreign key\n        ordersTable.Columns.Add("ProductName", typeof(string));\n\n        ds.Tables.Add(customersTable);\n        ds.Tables.Add(ordersTable);\n\n        // Define relationship (constraint)\n        DataRelation relation = new DataRelation(\n            "CustomerOrders",\n            customerIDColumn, \n            orderIDColumn,\n            false); // set to true if you want cascade delete\n\n        ds.Relations.Add(relation);\n\n        // Example data insert\n        customersTable.Rows.Add(1, "Alice");\n        ordersTable.Rows.Add(101, 1, "Widget");\n\n        // Test constraint enforcement (will throw exception)\n        try\n        {\n            ordersTable.Rows.Add(102, 2, "Gadget"); // Customer 2 doesn't exist\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine(ex.Message);\n        }\n    }\n}