using System;\nusing System.Xml;\nusing System.Security;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string inputUserName = GetUserInput("username");\n        string inputEmail = GetUserInput("email");\n\n        // Sanitize the inputs\n        string safeUserName = SanitizeInput(inputUserName);\n        string safeEmail = SanitizeInput(inputEmail);\n\n        XmlTextWriter writer = new XmlTextWriter("filename.xml", null);\n\n        writer.WriteStartElement("User");\n        writer.WriteElementString("Username", safeUserName);\n        writer.WriteElementString("Email", safeEmail);\n        writer.WriteEndElement();\n\n        writer.Close();\n    }\n\n    private static string GetUserInput(string prompt)\n    {\n        // Simulate getting user input\n        Console.WriteLine($"Enter {prompt}:");\n        return Console.ReadLine();\n    }\n\n    private static string SanitizeInput(string input)\n    {\n        if (input == null) return null;\n\n        // Example: Trim whitespace and enforce character set rules\n        string sanitized = input.Trim();\n\n        // Further sanitization can be added here, like removing or encoding specific characters\n        // For security, avoid reinventing the wheel and use well-established libraries or methods\n\n        // Example: Replace potentially unsafe sequences (this is just an example, not exhaustive)\n        sanitized = SecurityElement.Escape(sanitized);\n\n        return sanitized;\n    }\n}