using System;\nusing System.Data;\nusing System.Data.SqlClient;\n\npublic class AdoNetExample {\n    private string connectionString = "your_connection_string_here";\n\n    public void LoadData() {\n        using (SqlConnection conn = new SqlConnection(connectionString)) {\n            SqlDataAdapter adapter = new SqlDataAdapter("SELECT * FROM Employees", conn);\n            DataSet dataSet = new DataSet();\n\n            // Fill the DataSet with data from the database\n            adapter.Fill(dataSet, "Employees");\n\n            // Work with the DataSet in memory\n            foreach (DataRow row in dataSet.Tables["Employees"].Rows) {\n                Console.WriteLine(row["EmployeeName"]);\n            }\n\n            // Update changes back to the database\n            SqlCommandBuilder builder = new SqlCommandBuilder(adapter);\n            adapter.Update(dataSet, "Employees");\n        }\n    }\n}