public void RefreshFlashControl()\n{\n    // Ensure axFlash has been properly disposed of.\n    if (axFlash != null)\n    {\n        try\n        {\n            axFlash.Stop();\n            axFlash.Dispose();\n        }\n        catch { /* Log exceptions or handle as appropriate */ }\n        \n        // Remove the control from its parent container to ensure it gets cleaned up.\n        Controls.Remove(axFlash);\n        \n        // Nullify reference and allow for garbage collection.\n        axFlash = null;\n    }\n\n    // Reinitialize the ShockwaveFlashObject\n    axFlash = new AxShockwaveFlashObjects.AxShockwaveFlash();\n    axFlash.Dock = DockStyle.Fill;\n\n    // Add the control back to its parent container\n    Controls.Add(axFlash);\n\n    // Bring it to front and load a new movie.\n    axFlash.BringToFront();\n    axFlash.Movie = scheduleItem.FilePath;\n    axFlash.Show();\n    axFlash.Play();\n}