using System;\nusing System.Windows.Forms;\n\nclass Program\n{\n    [STAThread]\n    static void Main()\n    {\n        Application.EnableVisualStyles();\n        string result = ShowInputDialog("Enter your input:", "Input Dialog");\n        Console.WriteLine($"You entered: {result}");\n    }\n\n    static string ShowInputDialog(string title, string promptText)\n    {\n        Form form = new Form();\n        Label label = new Label() { Parent = form, Text = promptText, AutoSize = true };\n        TextBox textBox = new TextBox() { Parent = form, Dock = DockStyle.Fill, Top = label.Bottom + 6 };\n        Button confirmation = new Button() { Text = "Ok", DialogResultSet = DialogResult.OK, Anchor = AnchorStyles.Bottom | AnchorStyles.Right };\n\n        cancellation.Text = "Cancel";\n        cancellation.DialogResult = DialogResult.Cancel;\n        cancellation.Anchor = AnchorStyles.Bottom | AnchorStyles.Right;\n\n        label.Top = (form.Height - label.Bottom) / 2 + form.Top + 50; // Center the label\n        textBox.Location = new System.Drawing.Point(12, label.Height + 24); // Position the textbox below the label\n        cancellation.Location = new System.Drawing.Point(textBox.Width - cancellation.Width - 12, textBox.Height + label.Height + 36);\n\n        form.ClientSize = new System.Drawing.Size(Math.Max(form.ClientSize.Width, confirmation.Right + 12), textBox.Bottom + 12);\n        form.AcceptButton = confirmation;\n\n        return form.ShowDialog() == DialogResult.OK ? textBox.Text : "";\n    }\n}