using System.IO;\nusing System.Net;\nusing System.Net.Http;\nusing System.Threading.Tasks;\nusing System.Web.Http;\n\npublic class ReportsController : ApiController\n{\n    [HttpGet]\n    public async Task<HttpResponseMessage> GetExcelReport()\n    {\n        var filePath = @"C:\Path\To\Your\Report.xlsx";\n\n        if (!File.Exists(filePath))\n        {\n            return Request.CreateErrorResponse(HttpStatusCode.NotFound, "File not found");\n        }\n\n        var stream = new FileStream(filePath, FileMode.Open, FileAccess.Read);\n        var result = new HttpResponseMessage(HttpStatusCode.OK)\n        {\n            Content = new StreamContent(stream)\n        };\n        \n        // Set the content headers\n        result.Content.Headers.ContentType = \n            System.Net.Http.Headers.MediaTypeHeaderValue.Parse("application/vnd.openxmlformats-officedocument.spreadsheetml.sheet");\n        result.Content.Headers.ContentDisposition = \n            new System.Net.Http.Headers.ContentDispositionHeaderValue("attachment")\n        {\n            FileName = "Report.xlsx"\n        };\n\n        return result;\n    }\n}