public static int GetEncryptedLength(int plainTextSize)\n{\n    // Rijndael block size is 16 bytes\n    const int blockSizeInBytes = 16;\n    \n    // Calculate number of blocks required for the plaintext, including padding\n    int numBlocks = (int)Math.Ceiling((double)plainTextSize / blockSizeInBytes);\n    int PaddedPlaintextLength = numBlocks * blockSizeInBytes;\n\n    // Each byte requires ~4/3 characters in Base64\n    double base64EncodedLength = Math.Ceiling((double)PaddedPlaintextLength / 3 * 4);\n\n    return (int)base64EncodedLength;\n}