using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    // Declare the necessary Win32 functions and constants\n    [DllImport("Your32BitDll.dll", CallingConvention = CallingConvention.StdCall)]\n    public static extern int YourFunction();\n\n    static void Main(string[] args)\n    {\n        try\n        {\n            int result = YourFunction();\n            Console.WriteLine("Result from DLL: " + result);\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("An error occurred: " + ex.Message);\n        }\n    }\n}