using System;\nusing System.Diagnostics;\n\nclass Program {\n    static void Main() {\n        Console.WriteLine("OS Version: " + Environment.OSVersion);\n\n        // Get memory info\n        var memInfo = new PerformanceCounter("Memory", "Available Bytes");\n        Console.WriteLine($"Available Memory (bytes): {memInfo.NextValue()}");\n\n        // List processes\n        foreach (Process process in Process.GetProcesses()) {\n            Console.WriteLine($"{process.ProcessName} ({process.Id})");\n        }\n    }\n}