protected void ProcessOrderButton_Click(object sender, EventArgs e)\n{\n    foreach (GridViewRow row in GridView1.Rows)\n    {\n        CheckBox chkSelect = (CheckBox)row.FindControl("chkSelect"); // Replace "chkSelect" with your checkbox ID\n        TextBox txtQuantity = (TextBox)row.FindControl("txtQuantity"); // Replace "txtQuantity" with your textbox ID\n        Label lblAvailableQty = (Label)row.FindControl("lblAvailableQty"); // Replace "lblAvailableQty" with your label ID for Quantity\n\n        if (chkSelect != null && chkSelect.Checked)\n        {\n            int reqQuantity;\n            bool isValidInt = int.TryParse(txtQuantity.Text, out reqQuantity);\n            int availableQuantity = Convert.ToInt32(lblAvailableQty.Text);\n\n            if (isValidInt)\n            {\n                if (reqQuantity <= availableQuantity)\n                {\n                    // Proceed with your order logic for this product\n                    // For example, you can add details to an order list or a database\n                    ProcessOrder(row);\n                }\n                else\n                {\n                    // Handle the case where requested quantity exceeds available quantity\n                    // Optionally display a message to the user\n                    Label validationLabel = (Label)row.FindControl("validationLabel"); // Replace with your label ID for displaying errors\n                    if (validationLabel != null)\n                    {\n                        validationLabel.Text = "Requested quantity exceeds available stock.";\n                    }\n                }\n            }\n            else\n            {\n                // Handle invalid quantity input\n                Label validationLabel = (Label)row.FindControl("validationLabel");\n                if (validationLabel != null)\n                {\n                    validationLabel.Text = "Please enter a valid quantity.";\n                }\n            }\n        }\n    }\n}\n\nprivate void ProcessOrder(GridViewRow row)\n{\n    // Implement your order processing logic here\n    // For example, fetching product details and adding them to an order object or database\n}