void Application_BeginRequest(object sender, EventArgs e)\n{\n    if (HttpContext.Current.Session == null)\n    {\n        // Log this occurrence for debugging purposes.\n        // You might want to use a logging framework or write directly to a file/db.\n        string info = $"Session is null. User-Agent: {HttpContext.Current.Request.UserAgent}";\n        System.IO.File.AppendAllText("session_errors.log", info + Environment.NewLine);\n    }\n}