using System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string workspacePath = @"C:\path\to\new\workspace";\n\n        // Create the workspace directory\n        Directory.CreateDirectory(workspacePath);\n\n        // Create .metadata and its subdirectories\n        Directory.CreateDirectory(Path.Combine(workspacePath, ".metadata", ".plugins", "org.eclipse.core.resources"));\n\n        // Optionally, create a .location file with the path to itself (though not necessary for just structure)\n        File.WriteAllText(\n            Path.Combine(workspacePath, ".metadata", ".plugins", "org.eclipse.core.resources", ".location"),\n            Convert.ToBase64String(System.Text.Encoding.UTF8.GetBytes(workspacePath))\n        );\n\n        Console.WriteLine("Workspace created!");\n    }\n}