using OpenQA.Selenium;\nusing OpenQA.Selenium.Chrome;\nusing System;\nusing System.Collections.Generic;\n\npublic void hladame_fieldy()\n{\n    // Initialize the Chrome WebDriver (make sure to specify the path to chromedriver)\n    IWebDriver driver = new ChromeDriver();\n\n    // Navigate to your target URL\n    driver.Navigate().GoToUrl("http://your-target-url.com");\n\n    // Find all input fields with type 'text'\n    IList<IWebElement> inputFields = driver.FindElements(By.XPath("//input[@type='text']"));\n\n    // Cycle through each input field and get its name attribute\n    foreach (IWebElement field in inputFields)\n    {\n        string nazov_fieldu = field.GetAttribute("name");\n        Console.WriteLine(nazov_fieldu);\n    }\n\n    // Output the total count of text input fields\n    Console.WriteLine("Celkovy pocet fieldov je = " + inputFields.Count);\n\n    // Close the browser\n    driver.Quit();\n}