using Gtk;\n\npublic class UriFileChooser : Window\n{\n    private Entry uriEntry;\n    \n    public UriFileChooser() : base("URI File Chooser")\n    {\n        SetDefaultSize(400, 100);\n        \n        uriEntry = new Entry();\n        uriEntry.PlaceholderText = "Enter URI (http://...)";\n        uriEntry.Activated += OnUriActivated;\n\n        var vbox = new Box(Orientation.Vertical, Spacing: 6);\n        vbox.PackStart(new Label("Please enter a file URL:"), expand: false, fill: false, padding: 0);\n        vbox.PackStart(uriEntry, expand: true, fill: true, padding: 0);\n        \n        Add(vbox);\n    }\n\n    private void OnUriActivated(object sender, System.EventArgs e)\n    {\n        string uri = uriEntry.Text;\n        Console.WriteLine($"Selected URI: {uri}");\n        // Here you can handle the URI as needed\n    }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        Application.Init();\n        new UriFileChooser().ShowAll();\n        Application.Run();\n    }\n}