using System;\nusing MailKit.Net.Smtp;\nusing MimeKit;\n\nnamespace EmailSender\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            var message = new MimeMessage();\n            message.From.Add(new MailboxAddress("Your Name", "yourname@example.com"));\n            message.To.Add(MailboxAddress.Parse("recipient@example.com"));\n            message.Subject = "Test Email";\n\n            message.Body = new TextPart("plain")\n            {\n                Text = @"Hello, this is a test email sent using MailKit with implicit SSL."\n            };\n\n            using (var client = new SmtpClient())\n            {\n                // For demo purposes, accept all SSL certificates (in case the server supports STARTTLS)\n                client.ServerCertificateValidationCallback = (s, c, h, e) => true;\n\n                client.Connect("tempurl.org", 465, MailKit.Security.SecureSocketOptions.Auto);\n\n                client.Authenticate("username", "password");\n\n                client.Send(message);\n                client.Disconnect(true);\n            }\n        }\n    }\n}