using Gtk;\n\npublic class MyWidget : Gtk.Container\n{\n    public MyWidget()\n    {\n        // Assuming you have a ScrolledWindow and Viewport already created and added.\n        var scrolledWindow = new ScrolledWindow();\n        var viewport = new Viewport();\n\n        Add(scrolledWindow);\n        scrolledWindow.Add(viewport);\n\n        // Connect the event handler to the Viewport\n        viewport.EventAfter += OnViewportEvent;\n    }\n\n    private bool OnViewportEvent(object o, Gdk.EventArgs args)\n    {\n        if (args.Event is Gdk.EventScroll scrollEvent)\n        {\n            // Your code here to handle the scroll event\n            Console.WriteLine($"Scrolled in direction: {scrollEvent.Direction}");\n            return true;  // Return true to stop further propagation of the event\n        }\n        \n        return false;   // Let the event continue to be handled elsewhere\n    }\n}