// Define a method to remove an item from all tracked sessions\npublic void RemoveItemFromAllSessions(string keyToRemove)\n{\n    // Assuming sessionKeys is a List<string> stored in application state\n    var sessionKeys = (List<string>)HttpContext.Current.Application["sessionKeys"];\n    if (sessionKeys != null)\n    {\n        foreach (var sessionKey in sessionKeys)\n        {\n            HttpApplicationState appState = HttpContext.Current.Application;\n            HttpSessionState session = SessionStateUtility.GetHttpSessionStateContext(appState).SessionIDManager.CreateSessionID(HttpContext.Current);\n            var currentSession = new HttpSessionStateContainer(session.ID, \n                                                             null, \n                                                             "/", \n                                                             false, \n                                                             session.Timeout);\n\n            // Manually access the state service to modify sessions\n            if (Session[itemToRemove] != null)\n            {\n                Session.Remove(itemToRemove);\n            }\n        }\n    }\n}