using System;\nusing System.Data.SqlClient;\n\n// Assuming myReader is an instance of SqlDataReader\nif (myReader.HasRows)\n{\n    int additionalIndex = -1;\n    for (int i = 0; i < myReader.FieldCount; i++)\n    {\n        if ("Additional".Equals(myReader.GetName(i), StringComparison.OrdinalIgnoreCase))\n        {\n            additionalIndex = i;\n            break;\n        }\n    }\n\n    if (additionalIndex != -1)\n    {\n        if (!myReader.IsDBNull(additionalIndex))\n        {\n            ltlAdditional.Text = myReader.GetString(additionalIndex);\n        }\n        else\n        {\n            ltlAdditional.Text = "is null";\n        }\n    }\n    else\n    {\n        // Handle the case when "Additional" column is not found\n        ltlAdditional.Text = "Column 'Additional' not found in result set.";\n    }\n}\nelse\n{\n    // Handle no rows returned case\n    ltlAdditional.Text = "No data available.";\n}