public static ISessionFactory CreateSessionFactory()\n{\n    var configuration = Fluently.Configure()\n        .Database(MsSqlConfiguration.MsSql2012.ConnectionString(c => c.FromConnectionStringWithKey("MyConnectionString")))\n        .Mappings(m =>\n        {\n            // Add mappings from the Core assembly\n            m.FluentMappings.AddFromAssemblyOf<Customer>();\n            \n            // Add mappings from the XTend assembly\n            m.FluentMappings.AddFromAssemblyOf<xCustomer>();\n            \n            // Optionally, you can add all your XML mapping files if you're using those as well\n            m.HbmMappings.AddFromAssemblyOf<Customer>();\n            m.HbmMappings.AddFromAssemblyOf<xCustomer>();\n        })\n        .BuildSessionFactory();\n    return configuration;\n}