using System;\nusing System.Threading;\nusing System.Windows.Forms;\n\npublic class Program\n{\n    [STAThread]\n    public static void Main()\n    {\n        Thread myFormThread = new Thread(new ThreadStart(MyFormThreadProc));\n        myFormThread.SetApartmentState(ApartmentState.STA);\n        myFormThread.Start();\n        \n        // Optionally, wait for the thread to complete if needed.\n        myFormThread.Join();\n    }\n\n    private static void MyFormThreadProc()\n    {\n        using (MyForm form = new MyForm())\n        {\n            Application.Run(form);\n        }\n    }\n}