using Microsoft.SharePoint.Client;\nusing System;\n\nnamespace EnableContentTypes\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            // Setup context and authentication\n            using (ClientContext context = new ClientContext("https://your-sharepoint-site"))\n            {\n                context.Credentials = new SharePointOnlineCredentials("username", GetSecureStringFromPlainText("password"));\n\n                // Get the list by title and enable content types\n                List list = context.Web.Lists.GetByTitle("Your Document Library Title");\n                list.ContentTypesEnabled = true;\n                list.Update();\n\n                // Execute the query to update settings on server\n                context.ExecuteQuery();\n            }\n        }\n\n        // Helper method to convert plain text password to secure string\n        static SecureString GetSecureStringFromPlainText(string password)\n        {\n            if (password == null)\n                throw new ArgumentNullException("password");\n\n            var securePassword = new SecureString();\n\n            foreach (char c in password)\n                securePassword.AppendChar(c);\n\n            return securePassword;\n        }\n    }\n}