// ORM-Generated Class\npublic class CustomerEntity\n{\n    public int Id { get; set; }\n    public string Name { get; set; }\n    // Other properties...\n}\n\n// Business Layer Class\npublic class Customer\n{\n    public int Id { get; set; }\n    public string FullName { get; set; }\n\n    public static Customer From(CustomerEntity entity)\n    {\n        return new Customer\n        {\n            Id = entity.Id,\n            FullName = entity.Name\n        };\n    }\n\n    // Business logic methods...\n}\n\n// Usage in a service or repository layer\npublic class CustomerService\n{\n    private readonly DbContext _dbContext;\n\n    public CustomerService(DbContext dbContext)\n    {\n        _dbContext = dbContext;\n    }\n\n    public Customer GetCustomerById(int id)\n    {\n        var customerEntity = _dbContext.Set<CustomerEntity>().Find(id);\n        if (customerEntity == null) return null;\n        \n        return Customer.From(customerEntity);\n    }\n}