using System;\nusing System.IO;\nusing System.Web;\n\npublic partial class DownloadLog : System.Web.UI.Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        // Assuming you want to trigger the download when the page loads\n        // You can also call it from a button click event like:\n        // Button1_Click(null, null);\n    }\n\n    protected void Button1_Click(object sender, EventArgs e)\n    {\n        string logFilePath = Server.MapPath("~/Logs/yourlogfile.log"); // replace with your file name\n\n        if (File.Exists(logFilePath))\n        {\n            Response.Clear();\n            Response.ContentType = "application/octet-stream";\n            Response.AddHeader("Content-Disposition", "attachment; filename=yourlogfile.log");\n            Response.WriteFile(logFilePath);\n            Response.Flush();\n            Response.End();\n        }\n        else\n        {\n            // Handle the case when file does not exist, e.g., show an error message to user.\n            throw new Exception("File does not exist.");\n        }\n    }\n}