internal class ObjManager\n{\n    internal List<Obj> returnStuff()\n    {\n        // Direct casting will fail, so we create a new list\n        List<ObjBase> returnedList = getSomeStuff();\n        List<Obj> listToReturn = new List<Obj>();\n\n        foreach (var item in returnedList)\n        {\n            if (item is Obj objItem)\n            {\n                listToReturn.Add(objItem);\n            }\n        }\n\n        return listToReturn;\n    }\n\n    private List<ObjBase> getSomeStuff()\n    {\n        // Assume this method returns a list of ObjBase\n        return new List<ObjBase>();\n    }\n}