using System;\nusing System.IO;\nusing System.Reflection;\n\nclass Program\n{\n    static void Main()\n    {\n        // Path to your icon file\n        string iconPath = @"C:\path\to\your\icon.ico";\n\n        // Load the Assembly containing the internal methods\n        Assembly assembly = Assembly.GetAssembly(typeof(System.Windows.Forms.Form));\n\n        if (assembly == null)\n        {\n            Console.WriteLine("Unable to load assembly.");\n            return;\n        }\n\n        // Locate and invoke the internal method to set the console icon\n        MethodInfo mi = assembly.GetType("System.Windows.Forms.SystemInformation, System.Windows.Forms")\n                                .GetMethod("SetConsoleIcon", BindingFlags.Static | BindingFlags.NonPublic);\n        \n        if (mi != null)\n        {\n            mi.Invoke(null, new object[] { File.ReadAllBytes(iconPath) });\n        }\n        else\n        {\n            Console.WriteLine("Method not found.");\n        }\n\n        // Your application logic here...\n    }\n}