using (SPSite site = new SPSite("http://your-site-url"))\n{\n    using (SPWeb web = site.OpenWeb())\n    {\n        SPList list = web.Lists["Your List Name"];\n\n        // Setting a custom property\n        list.RootFolder.Properties.Add("MyKey", "MyValue");\n        list.RootFolder.Update();\n\n        // Retrieving the custom property\n        if (list.RootFolder.Properties.ContainsKey("MyKey"))\n        {\n            string myValue = list.RootFolder.Properties["MyKey"].ToString();\n        }\n    }\n}