public class NodePair\n{\n    public int StartNode { get; set; }\n    public int EndNode { get; set; }\n\n    public NodePair(int startNode, int endNode)\n    {\n        StartNode = startNode;\n        EndNode = endNode;\n    }\n\n    // Optionally override ToString() and Equals/GetHashCode() for better readability and comparison\n}\n\n// Usage\nList<NodePair> pathfindingData = new List<NodePair>();\npathfindingData.Add(new NodePair(1, 2));\npathfindingData.Add(new NodePair(3, 4));\n\nforeach (var pair in pathfindingData)\n{\n    Console.WriteLine($"Start: {pair.StartNode}, End: {pair.EndNode}");\n}