using System.IO;\nusing System.Web.Mvc;\n\npublic class HomeController : Controller\n{\n    [HttpPost]\n    public ActionResult UploadFiles()\n    {\n        if (Request.Files.Count > 0)\n        {\n            var tempFile = Server.MapPath("~/Uploads/");\n            \n            Directory.CreateDirectory(tempFile);\n            \n            for (int i = 0; i < Request.Files.Count; i++)\n            {\n                HttpPostedFileBase uploadFile = Request.Files[i];\n                \n                if (uploadFile.ContentLength > 0)\n                {\n                    string savedFileName = Path.Combine(tempFile, Path.GetFileName(uploadFile.FileName));\n                    \n                    // To support streaming and progress tracking, you can read the file in chunks\n                    const int bufferSize = 4096;\n                    using (Stream inputStream = uploadFile.InputStream)\n                    using (Stream outputStream = new FileStream(savedFileName, FileMode.Create))\n                    {\n                        byte[] buffer = new byte[bufferSize];\n                        int bytesRead;\n                        \n                        while ((bytesRead = inputStream.Read(buffer, 0, bufferSize)) > 0)\n                        {\n                            outputStream.Write(buffer, 0, bytesRead);\n                            // You can send progress updates here if needed\n                        }\n                    }\n                }\n            }\n        }\n\n        return Json(new { success = true });\n    }\n}