using System;\nusing System.Net.Http;\nusing System.Threading.Tasks;\nusing Newtonsoft.Json.Linq;\n\nclass Program\n{\n    private const string apiKey = "YOUR_FLICKR_API_KEY";\n    private static readonly HttpClient client = new HttpClient();\n\n    static async Task Main(string[] args)\n    {\n        Console.WriteLine("Enter a keyword to search on Flickr:");\n        string keyword = Console.ReadLine();\n        \n        var imageUrl = await GetRandomImageUrlAsync(keyword);\n        \n        if (imageUrl != null)\n        {\n            Console.WriteLine($"Found image: {imageUrl}");\n            // You can add code here to download the image using the URL\n        }\n        else\n        {\n            Console.WriteLine("No images found.");\n        }\n    }\n\n    static async Task<string> GetRandomImageUrlAsync(string keyword)\n    {\n        var response = await client.GetStringAsync($"https://api.flickr.com/services/rest/?method=flickr.photos.search&api_key={apiKey}&text={keyword}&sort=random&per_page=1&format=json&nojsoncallback=1");\n        \n        dynamic data = JObject.Parse(response);\n        if (data.photos.photo.Count > 0)\n        {\n            var photo = data.photos.photo[0];\n            string imageUrl = $"https://farm{photo.farm}.staticflickr.com/{photo.server}/{photo.id}_{photo.secret}_m.jpg";\n            return imageUrl;\n        }\n        \n        return null;\n    }\n}