public static class SettingsManager\n{\n    // Example of static fields/properties\n    public static string ConfigSetting { get; private set; }\n    public static int InternalState { get; private set; }\n\n    static SettingsManager()\n    {\n        Reset(); // Initialize with default values\n    }\n\n    public static void SetConfigSetting(string value)\n    {\n        ConfigSetting = value;\n    }\n\n    public static void SetInternalState(int state)\n    {\n        InternalState = state;\n    }\n\n    public static void Reset()\n    {\n        // Reset all static fields/properties to their initial/default states\n        ConfigSetting = "default"; // Assuming a default value for the config setting\n        InternalState = 0; // Assuming a default value for internal state\n    }\n}