using System;\nusing System.Web;\n\npublic class MyHttpHandler : IHttpHandler\n{\n    public void ProcessRequest(HttpContext context)\n    {\n        // Access the cookie value by name\n        string myVariableValue = context.Request.Cookies["MyVariable"]?.Value;\n        \n        if (!string.IsNullOrEmpty(myVariableValue))\n        {\n            // Do something with your variable here\n            context.Response.Write($"Received: {myVariableValue}");\n        }\n        else\n        {\n            context.Response.Write("Cookie not found.");\n        }\n    }\n\n    public bool IsReusable => false;\n}