public class SafeStringModelBinder : DefaultModelBinder\n{\n    protected override void SetProperty(ControllerContext controllerContext, ModelBindingContext bindingContext, PropertyDescriptor propertyDescriptor, object value)\n    {\n        if (propertyDescriptor.PropertyType == typeof(string) && value is string s && !string.IsNullOrEmpty(s))\n        {\n            try\n            {\n                AntiXssEncoder.ThrowIfDangerousString(s);\n            }\n            catch(HttpRequestValidationException)\n            {\n                // Handle the exception or modify the value here\n                value = HttpUtility.HtmlEncode(value.ToString());\n            }\n        }\n\n        base.SetProperty(controllerContext, bindingContext, propertyDescriptor, value);\n    }\n}