using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\nclass WordDictionary {\n    private HashSet<string> words = new HashSet<string>();\n\n    public void AddWord(string word) {\n        words.Add(word.ToLower());\n    }\n\n    // Get words of a specific length\n    public IEnumerable<string> GetWordsOfLength(int length) {\n        return words.Where(word => word.Length == length);\n    }\n\n    // Get partial matches (words containing the substring)\n    public IEnumerable<string> GetPartialMatches(string partial) {\n        return words.Where(word => word.Contains(partial.ToLower()));\n    }\n}\n\nclass Program {\n    static void Main() {\n        WordDictionary dictionary = new WordDictionary();\n        dictionary.AddWord("apple");\n        dictionary.AddWord("application");\n        dictionary.AddWord("aptitude");\n\n        // Retrieve and print words of length 5\n        Console.WriteLine("Words of length 5:");\n        foreach (var word in dictionary.GetWordsOfLength(5)) {\n            Console.WriteLine(word);\n        }\n\n        // Retrieve and print partial matches for "app"\n        Console.WriteLine("\nPartial matches for 'app':");\n        foreach (var word in dictionary.GetPartialMatches("app")) {\n            Console.WriteLine(word);\n        }\n    }\n}