int maxWidth = W; // Width of your visualization area in pixels\nint maxHeight = H / 2; // Half height since we have two channels\ndouble scale = (maxHeight * 1.0) / 32768; // Scaling factor to fit data into the height\n\nfor(int i = 0; i < L - 1; i++)\n{\n    // Calculate Y positions for left and right channel samples, scaling down\n    int yLeft = (int)(maxHeight - (left[i] * scale)) + maxHeight; \n    int yRight= (int)(maxHeight - (right[i] * scale));\n\n    int nextYLeaf = (int)(maxHeight - (left[i + 1] * scale)) + maxHeight;\n    int nextYRight = (int)(maxHeight - (right[i + 1] * scale));\n\n    // Draw lines for both channels using normalized Y positions\n    DrawLine(Color.Blue, i, yLeft, i + 1, nextYLeaf); \n    DrawLine(Color.Red, i, yRight, i + 1, nextYRight);\n}