using System;\nusing System.Data;\n\nclass Program\n{\n    static void Main()\n    {\n        // Original DataTable\n        DataTable originalTable = new DataTable();\n        originalTable.Columns.Add("ID", typeof(int));\n        originalTable.Columns.Add("Name", typeof(string));\n        \n        // Clone the schema of the original DataTable to a new DataTable\n        DataTable newTable = originalTable.Clone();\n\n        // You can now use newTable which has the same schema as originalTable but no data\n    }\n}