public Color x, y;\nprotected void Page_Load(object sender, EventArgs e)\n{\n    BackGroundColorArranger(Color.Red, Color.Salmon, 10); // Passing 10 as the number of steps\n}\n\nvoid BackGroundColorArranger(Color startColor, Color endColor, int steps)\n{\n    for (int i = 0; i <= steps; i++)\n    {\n        float ratio = (float)i / steps;\n        int r = (int)Math.Round(startColor.R + ratio * (endColor.R - startColor.R));\n        int g = (int)Math.Round(startColor.G + ratio * (endColor.G - startColor.G));\n        int b = (int)Math.Round(startColor.B + ratio * (endColor.B - startColor.B));\n\n        Panel panel = new Panel();\n        panel.ID = i.ToString();\n        panel.Width = 150;\n        panel.Height = 50;\n        panel.BackColor = Color.FromArgb(r, g, b);\n        this.Controls.Add(panel);\n    }\n}