// This is a simple example of placing a ComboBox next to or inline with text.\nusing System.Windows;\nusing System.Windows.Controls;\nusing System.Windows.Documents;\n\nnamespace WpfApp\n{\n    public partial class MainWindow : Window\n    {\n        public MainWindow()\n        {\n            InitializeComponent();\n\n            // Create a flow document to hold text with UI elements.\n            var flowDocument = new FlowDocument();\n            var paragraph = new Paragraph();\n\n            // Regular text.\n            paragraph.Inlines.Add(new Run("Here is some text "));\n\n            // Add comboBox inline with text.\n            var comboBox = new ComboBox\n            {\n                ItemsSource = new[] { "Option1", "Option2", "Option3" },\n                Width = 100\n            };\n            \n            var inlineWithUIElement = new InlineUIContainer(comboBox);\n            paragraph.Inlines.Add(inlineWithUIElement);\n\n            // More text can be added after the ComboBox.\n            paragraph.Inlines.Add(new Run(" and some more text."));\n\n            flowDocument.Blocks.Add(paragraph);\n\n            // Set the RichTextBox's document to display it.\n            richTextControl.Document = flowDocument;\n        }\n    }\n}