using System;\nusing System.Net.Http;\nusing System.Text;\nusing System.Threading.Tasks;\nusing System.Collections.Generic;\n\nclass Program\n{\n    static async Task Main(string[] args)\n    {\n        // Create an HttpClient handler and client that can persist cookies across requests\n        var cookieContainer = new CookieContainer();\n        using (var handler = new HttpClientHandler() { CookieContainer = cookieContainer })\n        using (var client = new HttpClient(handler))\n        {\n            // Set the base address of the HTTP Client.\n            client.BaseAddress = new Uri("https://www.azet.sk/");\n\n            // Prepare login data\n            var loginData = new Dictionary<string, string>\n            {\n                { "username", "your_username" },\n                { "password", "your_password" }\n            };\n\n            // Create a FormUrlEncodedContent object.\n            var content = new FormUrlEncodedContent(loginData);\n\n            // Set the request method, URL for logging in, and POST data.\n            var loginResponse = await client.PostAsync("/login-url", content);\n            \n            if (loginResponse.IsSuccessStatusCode)\n            {\n                Console.WriteLine("Login successful!");\n\n                // Here you can make requests to other endpoints while logged in\n                var response = await client.GetAsync("/some-protected-page");\n                \n                string responseData = await response.Content.ReadAsStringAsync();\n                Console.WriteLine(responseData);\n            }\n            else\n            {\n                Console.WriteLine("Login failed.");\n            }\n        }\n    }\n}