// Example of calling API to start transaction\n    [SetUp]\n    public void Setup()\n    {\n        RestClient client = new RestClient("http://testsite/api/");\n        RestRequest request = new RestRequest("transaction/start", Method.POST);\n        client.Execute(request);\n    }\n\n    [TearDown]\n    public void Teardown()\n    {\n        RestClient client = new RestClient("http://testsite/api/");\n        RestRequest request = new RestRequest("transaction/rollback", Method.POST);\n        client.Execute(request);\n    }