using System;\nusing System.Web.Mvc;\n\npublic class PersonModelBinder : DefaultModelBinder\n{\n    public override object BindModel(ControllerContext controllerContext, ModelBindingContext bindingContext)\n    {\n        var person = (Person)base.BindModel(controllerContext, bindingContext);\n\n        var numberOfCatsValue = bindingContext.ValueProvider.GetValue("NumberOfCatsNamedEnder");\n\n        if (!int.TryParse(numberOfCatsValue.AttemptedValue, out _))\n        {\n            modelState.AddModelError("NumberOfCatsNamedEnder", "Ender count should be a number.");\n            person.NumberOfCatsNamedEnder = 0; // or any default value you prefer\n        }\n\n        return person;\n    }\n}