using Microsoft.Win32;\n\n// Open the registry key for reading only\nRegistryKey myKey = Registry.LocalMachine.OpenSubKey("SOFTWARE\\MyCompany\\MyAppName", false);\n\nif (myKey != null)\n{\n    // Read a value, e.g., "SomeValueName"\n    object value = myKey.GetValue("SomeValueName");\n    \n    if (value != null)\n    {\n        // Use the value as needed\n        Console.WriteLine("Read value: " + value.ToString());\n    }\n    \n    myKey.Close(); // Always close the registry key when done\n}\nelse\n{\n    Console.WriteLine("Registry key not found.");\n}