private void FindRecord(int comboBoxId, int treeNodeId)\n{\n    // Assuming your binding source is bound to a DataTable or DataView that has 'ComboBoxColumn' and 'TreeNodeColumn'\n    var dataTable = (DataTable)bindingSource1.DataSource;\n    \n    // Create a new view on the data table with a filter for both columns\n    DataView filteredView = new DataView(dataTable)\n    {\n        RowFilter = $"ComboBoxColumn = {comboBoxId} AND TreeNodeColumn = {treeNodeId}"\n    };\n\n    if (filteredView.Count > 0)\n    {\n        // Set the binding source to use the filtered view\n        bindingSource1.DataSource = filteredView;\n\n        // Move the current position to the first matching record\n        bindingSource1.Position = 0;\n    }\n    else\n    {\n        // Handle the case where no rows match the criteria\n        MessageBox.Show("No records found with the specified IDs.");\n    }\n}