using System;\nusing System.Web.Security;\n\n// Example method to retrieve timeout from database (dummy function)\nprivate static int GetTimeoutFromDatabase()\n{\n    // In real-world scenario, place your database retrieval logic here.\n    return 30; // Timeout in minutes, e.g., 30 minutes.\n}\n\npublic void LoginUser(string username, bool rememberMe)\n{\n    int timeoutInMinutes = GetTimeoutFromDatabase(); // Retrieve from database\n    var ticket = new FormsAuthenticationTicket(\n        version: 1,\n        name: username,\n        issued: DateTime.Now,\n        expires: DateTime.Now.AddMinutes(timeoutInMinutes),\n        isPersistent: rememberMe,\n        userData: "",\n        cookiePath: FormsAuthentication.FormsCookiePath);\n\n    string encryptedTicket = FormsAuthentication.Encrypt(ticket);\n    var authCookie = new HttpCookie(FormsAuthentication.FormsCookieName, encryptedTicket)\n    {\n        Expires = ticket.Expiration\n    };\n\n    HttpContext.Current.Response.Cookies.Add(authCookie); // Add the cookie to the response.\n}