// Example of using Dapper in C#\nusing System.Data;\nusing MySql.Data.MySqlClient;\nusing Dapper;\n\npublic class Person\n{\n    public int Id { get; set; }\n    public string FirstName { get; set; }\n    public string LastName { get; set; }\n    public string FullName => $"{FirstName} {LastName}";\n}\n\nvar connectionString = "server=localhost;database=testdb;user=root;password=yourpassword";\nusing (IDbConnection dbConnection = new MySqlConnection(connectionString))\n{\n    var person = dbConnection.QueryFirstOrDefault<Person>("SELECT * FROM People WHERE Id = @Id", new { Id = 1 });\n}