using System;\nusing Twilio;\nusing Twilio.Base;\nusing Twilio.Rest.Lookups.V1;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Authenticate with Twilio.\n        const string accountSid = "your_account_sid";\n        const string authToken = "your_auth_token";\n\n        TwilioClient.Init(accountSid, authToken);\n\n        try\n        {\n            // Use the PhoneNumbers resource to look up a phone number.\n            var phoneNumberInstance = PhoneNumberResource.Fetch(\n                pathPhoneNumber: new Twilio.Types.PhoneNumber("+1234567890")\n            );\n\n            Console.WriteLine("Phone number is valid and exists.");\n            \n            if (phoneNumberInstance.Carrier != null)\n            {\n                Console.WriteLine($"Carrier: {phoneNumberInstance.Carrier.Name}");\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Error validating phone number: {ex.Message}");\n        }\n    }\n}