using System;\nusing System.Net.Http;\nusing System.Text;\nusing Newtonsoft.Json;\n\npublic class CreateSubtaskExample\n{\n    public static async Task Main(string[] args)\n    {\n        // Set up the HttpClient with authentication headers.\n        var httpClient = new HttpClient();\n        var authString = "username:apikey"; // Replace 'username' and 'apikey' with your JIRA username (email) and API key\n        var encodedBytes = Encoding.UTF8.GetBytes(authString);\n        var base64AuthString = Convert.ToBase64String(encodedBytes);\n        httpClient.DefaultRequestHeaders.Add("Authorization", $"Basic {base64AuthString}");\n        httpClient.DefaultRequestHeaders.Add("Content-Type", "application/json");\n\n        // Prepare the data for the subtask.\n        var subtaskData = new\n        {\n            fields = new\n            {\n                project = new { key = "PROJ" }, // Replace 'PROJ' with your project key\n                summary = "This is a test subtask",\n                issuetype = new { name = "Sub-task" },\n                parent = new { id = "10000" } // Replace '10000' with the ID of the parent issue\n            }\n        };\n\n        // Convert the data to JSON.\n        var jsonContent = JsonConvert.SerializeObject(subtaskData);\n        var content = new StringContent(jsonContent, Encoding.UTF8, "application/json");\n\n        // Make a POST request to create the subtask.\n        var response = await httpClient.PostAsync("https://yourdomain.atlassian.net/rest/api/3/issue", content); // Replace 'yourdomain' with your JIRA domain\n\n        if (response.IsSuccessStatusCode)\n        {\n            Console.WriteLine("Subtask created successfully.");\n        }\n        else\n        {\n            Console.WriteLine($"Failed to create subtask. Status code: {response.StatusCode}");\n        }\n    }\n}